import "./GiftsList.css";
// import { Link } from 'react-router-dom'
import axios from "axios";
import { useEffect, useState } from "react";

function GiftsList() {
  const [gift, setGift] = useState([]);

  // useEffect(() => {
  //     async function fetchGift() {
  //         try {
  //             const response = await axios.get("https://ironrest.herokuapp.com/gift");
  //             setGift({... response.data});
  //             console.log(gift)
  //         } catch (err){
  //             console.error(err)
  //         }
  //     }

  //     fetchGift();

  // },[])

  useEffect(() => {
    axios
      .get("https://ironrest.herokuapp.com/gift")
      .then((response) => {
        setGift([...response.data]);
        console.log(response.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div className="container">
      {gift.map((currentGift) => {
        return (
          <section key={currentGift._id} className="content">
            <img src={currentGift.imageUrl} alt="Current Gift" />
            <div className="content-text">
              <h2 className="mt-1">{currentGift.name}</h2>
              <p className="text-description">
                Craft notebook for you to gift your friends and family. It's
                super easy and fun to make
              </p>
              <p className="text-bold">
                Price: $$$ <br /> Skill Level: Beginner{" "}
              </p>
            </div>
          </section>
        );
      })}
    </div>
  );
}

export default GiftsList;
