    //             setGift({... response.data});
    //             console.log(gift)
    //         } catch (err){
    //             console.error(err)
    //         }
    //     }

    //     fetchGift();

    // },[])

    useEffect(() => {
        axios.get("https://ironrest.herokuapp.com/gift")
        .then((response) => {
            setGift([...response.data]);
            console.log(gift.imageUrl)
        }).catch((err) => {
            console.log(err)
        })
    },[])


  return (
    <div className="container">
      <section className="content">
        <img
          src={gift.imageUrl}
          alt=""
        />
        <div className="content-text">
          <h2 className="mt-1">Notebook</h2>
          <p className="text-description">
            Craft notebook for you to gift your friends and family. It's
            super easy and fun to make
          </p>
          <p className="text-bold">Price: $$$ <br/> Skill Level: Beginner </p>
        </div>
      </section>
    </div>
  );
}

export default GiftsList;
