import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import redLineImg from "./assets/red-line.png";
import randomImg from "./assets/message-img.jpeg";
import "./assets/RandomMessage.css";
import Navbar from "./Navbar";
import Loading from "./Loading";

function RandomMessage() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(false);
  }, []);

  return (
    <>
      {loading ? (
        <Loading />
      ) : (
        <>
          <Navbar />
          <div>
            <section id="main-text">
              <img
                className="red-line text-position"
                src={redLineImg}
                alt="red line"
              />
              <h1 className="pb-3">
                Have <span className="red-text">gift</span> ideas for this{" "}
                <span className="red-text">Xmas</span>
              </h1>
              <p className="text pb-3">
                put a little of yourself in your gifts. here you will find great
                ideas for gifting your loved ones in a creative and different
                way. try to get your hands dirty and be amazed. gift it
                yourself!
              </p>
              <Link to="/giftslist">
                <button
                  type="button"
                  className="text btn btn-dark rounded-pill"
                >
                  click here
                </button>
              </Link>
            </section>

            <section id="main-img">
              <div className="black-line"></div>
              <img className="img" src={randomImg} alt="gift-wrapped" />
              <img
                className="red-line image-position"
                src={redLineImg}
                alt="red line"
              />
            </section>
          </div>
        </>
      )}
    </>
  );
}

export default RandomMessage;
