import "./GiftsList.css";
// import { Link } from 'react-router-dom'
import axios from "axios";
import { useEffect, useState } from "react";

function GiftsList() {
  const [gift, setGift] = useState([]);

  useEffect(() => {
    async function fetchGift() {
      try {
        const response = await axios.get("https://ironrest.herokuapp.com/gift");
        setGift([...response.data]);
        console.log(gift);
      } catch (err) {
        console.error(err);
      }
    }

    fetchGift();
  }, []);

  return (
    <div className="container">
      {gift.map((currentGift) => {
        return (
          <section key={currentGift._id} className="content">
            <img src={currentGift.imageUrl} alt="Current Gift" />
            <div className="content-text">
              <h2 className="mt-1">{currentGift.title}</h2>
              <p className="text-description">{currentGift.description}</p>
              <p className="text-bold">
                Price: {currentGift.price} <br /> Skill Level:{" "}
                {currentGift.skillLevel}
              </p>
            </div>
          </section>
        );
      })}
    </div>
  );
}

export default GiftsList;
